mapper.select:
SELECT m_idx, m_email, m_nm, m_photo, m_regdate, m_quitdate, m_blacklist,(select count(*) FROM member m join report rep on m.m_idx=rep.m_reported WHERE memrow.m_idx=m.m_idx ) as reportCount FROM member memrow
WHERE memrow.m_blacklist='Y'
limit #{firstMemberIndexOfSelectedPage}, #{numOfMemberPerPage}

mapper.update:

	UPDATE member
	SET
	
		m_nm=#{m_nm}
	
	
		m_photo=#{m_photo}
	
	
		m_password=#{m_password}
	
	
	WHERE m_idx=#{m_idx}

	UPDATE member 
	SET
	tempcode=#{arg0}
	WHERE m_email=#{arg1}


	UPDATE member
	SET
	m_password=#{arg1}
	WHERE tempcode=#{arg0}
