mapper.select:
SELECT count(*) FROM qna q1 WHERE q1.qna_deldate is null
AND (select count(*) from qna q2 join reply on reply.qna_idx=q2.qna_idx and q2.qna_idx=q1.qna_idx)<1
AND q1.m_idx=#{arg0}

SELECT qna_idx, qna_title, date_format(qna_regdate, '%Y-%m-%d') AS qna_regdate FROM qna q1 WHERE q1.qna_deldate is null
AND (select count(*) from qna q2 join reply on reply.qna_idx=q2.qna_idx and q2.qna_idx=q1.qna_idx)<1
AND q1.m_idx=#{m_idx}
order by q1.qna_regdate desc limit #{selectPageFirstIndex},#{numOfQnaPerPage}
